s = ''

f = open("codon_table")

raw = f.readlines()

list = []
codonTable = {}

for i in range(len(raw)):
    list.append(raw[i].split())

for i in range(len(list)):
    for j in range(len(list[i])):
        if j%2==0:
            codonTable[list[i][j]] = list[i][j + 1]

output=''

for j in range(len(s)):
    if j % 3 == 0:
        temp=s[j]+s[j+1]+s[j+2]
        output=output+codonTable[temp]


print(len(s))

print(output)
